var fs = require( 'fs' );
var setter = require( './negation-setter.js' );

var loadNERModel = function () {
  var machines = fs.readFileSync( require.resolve( './languages/cur/models/eng-negation-model.json' ), 'utf8' ); // eslint-disable-line no-sync
  machines = JSON.parse( machines );

  return {
    machines: machines,
    setter: setter
  };
};

module.exports = loadNERModel;
