var fs = require( 'fs' );
var transformers = require( './ner-transformers.js' );

var loadNERModel = function () {
  var machines = fs.readFileSync( require.resolve( './languages/cur/models/eng-ner-model.json' ), 'utf8' ); // eslint-disable-line no-sync
  machines = JSON.parse( machines );

  return {
    machines: machines,
    transformers: transformers
  };
};

module.exports = loadNERModel;
