var fs = require( 'fs' );
// var setter = require( './neg-setter.js' );

var loadSAModel = function () {
  var machines = fs.readFileSync( require.resolve( './languages/cur/models/eng-sa-model.json' ), 'utf8' ); // eslint-disable-line no-sync
  machines = JSON.parse( machines );

  return { machines: machines };
};

module.exports = loadSAModel;
